        public override bool Interact(GamePlayer player)
        {
            if (!base.Interact(player)) return false;
            if (player.Level < 50) SendReply(player, "Please come back when you are level 50!");
            else SendReply(player, "Hello! " + player.Name + ", are you interested in some :\n\n   [Cloak] , [Weapons] ?\n");
            return true;
        }

